#!/usr/bin/python
# -*- coding: UTF-8 -*-

from PIL import Image
import matplotlib.pyplot as plt
import numpy as np
import cv2

def read_image_return_nparray(fname):
    image = Image.open(fname)
    image_array = np.array(image)
    return image_array

def resize_image_innparray(data, width, height):
    resized_image = cv2.resize(data, (width, height))
    return resized_image

def image_add_with_certain_weight(foreground, background, alpha, cmap='rainbow', verbose=False, save=None):
    """ About cmap , you can follow the link: https://matplotlib.org/3.1.0/tutorials/colors/colormaps.html"""

    fig = plt.figure(frameon=False)
    fig.set_size_inches(foreground.shape[1], foreground.shape[0])
    ax = plt.Axes(fig, [0., 0., 1., 1.])
    ax.set_axis_off()
    fig.add_axes(ax)
    ax.imshow(background)
    ax.imshow(255 * foreground, alpha=alpha, cmap=cmap)

    if save is not None:
        if verbose:
            print('save image: ' + save)
        fig.savefig(save, dpi=1, bbox_inches='tight', pad_inches=0)
